<form action="index.php?ctl=trading/deliveryarea&act=updateOrderNum" id="catEditor" method="post" target="{update:'messagebox'}"> <!--<div class="action-bar mainHead"><span class="sysiconBtn addorder" onclick="W.page('index.php?ctl=goods/category&act=addNew')">添加分类</span></div> --> <div class="action-bar mainHead"><a href="index.php?ctl=trading/deliveryarea&act=showNewArea" class="sysiconBtn addorder">添加顶级地区</a></div> <table cellspacing="0" cellpadding="0" class="gridlist"> <col class="Colauto"></col> <col class="Coloption_1b"></col> <col class="Coloption_1b" ></col> <col class="Coloption_1b"></col> <col class="Coloption_1b"></col> <col></col> <thead> <th>地区名称</th> <th>排序</th> <th>添加子地区</th> <th>编辑</th> <th>删除</th> <th></th> </thead> <tbody> <?php $this->_env_vars['foreach']["item"]=array('total'=>count($this->_vars['area']),'iteration'=>0);foreach ((array)$this->_vars['area'] as $this->_vars['iLoop'] => $this->_vars['item']){ $this->_env_vars['foreach']["item"]['first'] = ($this->_env_vars['foreach']["item"]['iteration']==0); $this->_env_vars['foreach']["item"]['iteration']++; $this->_env_vars['foreach']["item"]['last'] = ($this->_env_vars['foreach']["item"]['iteration']==$this->_env_vars['foreach']["item"]['total']); ?> <tr parentid="<?php echo $this->_vars['item']['region_id']; ?>"> <td style="text-align:left; width:320px; overflow:hidden;"> <div style="padding-left:<?php echo $this->_vars['item']['step']*25; ?>px"> <?php if( $this->_vars['item']['child_count']>0 ){ ?> <img class="imgTree" onclick="clickTree(this)" id=<?php echo $this->_vars['item']['region_id']; ?> src="images/sitemapclosed.gif" /> <?php }else{ ?> <img id=<?php echo $this->_vars['item']['region_id']; ?> src="images/sitemapopened.gif" /> <?php } ?> <a style="font-weight:700; color:#000; text-decoration:none;padding-right:15px;" href='index.php?ctl=trading/deliveryarea&act=detailDlArea&p[0]=<?php echo $this->_vars['item']['region_id']; ?>'><?php echo $this->_vars['item']['local_name']; ?></a></div> </th> <td style="width:80px"><input class="_x_ipt" vtype="unsignedint" type="number" size="2" style="width:30px;" name="p_order[<?php echo $this->_vars['item']['region_id']; ?>]" value="<?php echo $this->_vars['item']['ordernum']; ?>" caution="请输入正整数"></td> <td style="width:100px;"><?php $this->_vars["region_id"]=$this->_vars['item']['region_id']; ?> <a href='index.php?ctl=trading/deliveryarea&act=showNewArea&p[0]=<?php echo $this->_vars['region_id']; ?>'><img src="images/transparent.gif" border="0" alt="添加子地区" class="imgbundle" style="width:16px;height:16px;background-position:0 -0px;" /></a> </td> <td style="width:60px;"><a class="i" href='index.php?ctl=trading/deliveryarea&act=detailDlArea&p[0]=<?php echo $this->_vars['item']['region_id']; ?>'><img src="images/transparent.gif" border="0" alt="编辑" class="imgbundle" style="width:16px;height:16px;background-position:0 -284px;" /></a></td> <td style="width:60px;"><span onclick="javascript: if(confirm('您确定要删除&nbsp;[<?php echo $this->_vars['item']['local_name']; ?>]&nbsp;？')){ W.page('index.php?ctl=trading/deliveryarea&act=toRemoveArea&p[0]=<?php echo $this->_vars['item']['region_id']; ?>',{method:'get'});}" style="cursor:pointer"><img src="images/transparent.gif" border="0" alt="删除" class="imgbundle" style="width:16px;height:16px;background-position:0 -214px;" /></span></Td> <td></td> </tr> <?php } unset($this->_env_vars['foreach']["item"]); ?> </tbody ></table> <div class="mainFoot"> <div class="footer"> <div class="table-action"> <button type="submit" onclick="$('catEditor').fireEvent('submit',{stop:function(){}})" class="btn"><span><span>保存</span></span></button> </div> </div> </div> </form> <img id=imgTreeC style="display:none" src="images/sitemapclosed.gif" /> <img id=imgTreeO style="display:none" src="images/sitemapopened.gif" /> <script>
function clickTree(ipt){
  var obj = $(ipt).getParent('[parentid]');
  if($(ipt).src == $('imgTreeC').src && (!obj.getNext() || !obj.getNext().get('readstatus'))){
    new Element('tr',{'readstatus':1}).adopt(new Element('td').setProperties({'colspan':'6','style':'border:none'}).setStyle('padding',0)).injectAfter(obj);
    
    W.page('index.php?ctl=trading/deliveryarea&act=getChildNode',{update:obj.nextSibling.firstChild,method:'post',data:'regionId='+$(ipt).id});
  }else{
      if($(ipt).src == $('imgTreeC').src){
        obj.getNext().style.display = '';
      }else{
        obj.getNext().style.display = 'none';
      }
  }

  if($(ipt).src == $('imgTreeC').src) $(ipt).src = $('imgTreeO').src;
  else $(ipt).src = $('imgTreeC').src;
}
</script>